import React, { useState } from 'react';
import { View, Image, Text, StyleSheet, ImageSourcePropType } from 'react-native';
import { PanGestureHandler } from 'react-native-gesture-handler';
import { RevealPopup } from './RevealPopup';

interface ScratchCardProps {
  image: ImageSourcePropType;
  isSelected: boolean;
}

export default function ScratchCard({ image, isSelected }: ScratchCardProps) {
  const [scratched, setScratched] = useState(false);
  const [scratchProgress, setScratchProgress] = useState(0);
  const [showPopup, setShowPopup] = useState(false);

  const handleScratch = (event) => {
    if (isSelected) {
      setScratchProgress((prev) => Math.min(prev + 0.1, 1));
      if (scratchProgress >= 0.7 && !scratched) {
        setScratched(true);
        setShowPopup(true);
      }
    }
  };

  const handleClosePopup = () => {
    setShowPopup(false);
    setScratched(false);
    setScratchProgress(0);
  };

  return (
    <>
      <PanGestureHandler onGestureEvent={handleScratch}>
        <View style={styles.container}>
          <Image 
            source={image}
            style={styles.image}
            resizeMode="cover"
          />
          {!scratched && (
            <View style={[styles.overlay, { opacity: 1 - scratchProgress }]}>
              <Text style={styles.scratchText}>
                {isSelected ? 'Scratch here!' : 'Select me!'}
              </Text>
            </View>
          )}
        </View>
      </PanGestureHandler>
<Text>Play Ano</Text>
      <RevealPopup
        visible={showPopup}
        image={image}
        onClose={handleClosePopup}
      />
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    borderRadius: 15,
    overflow: 'hidden',
    backgroundColor: 'white',
  },
  image: {
    width: '100%',
    height: '100%',
    position: 'absolute',
  },
  overlay: {
    ...StyleSheet.absoluteFillObject,
    backgroundColor: '#4ECDC4',
    justifyContent: 'center',
    alignItems: 'center',
  },
  scratchText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
  },
});
