import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, ScrollView } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import ScratchCard from '../components/ScratchCard';

export default function GameScreen() {
  const [selectedCard, setSelectedCard] = useState(null);

  const cards = [
    { 
      id: 1, 
      imagePath: require('../../assets/full-nelson-sex-position.jpg') // Update with the correct relative path
    },
    { 
      id: 2, 
      imagePath: require('../../assets/Insatiable-clitoris-scaled.jpg')
    },
    { 
      id: 3, 
      imagePath: require('../../')
    },
    { 
      id: 4, 
      imagePath: require('../assets/images/image4.jpg')
    },
    { 
      id: 5, 
      imagePath: require('../assets/images/image5.jpg')
    },
    { 
      id: 6, 
      imagePath: require('../assets/images/image6.jpg')
    },
  ];


  return (
    <LinearGradient
      colors={['#FF6B6B', '#4ECDC4']}
      style={styles.container}
    >
      <Text style={styles.title}>Adventure Cards</Text>
      <Text style={styles.subtitle}>Scratch to reveal your next adventure!</Text>

      <ScrollView contentContainerStyle={styles.cardsContainer}>
        {cards.map((card) => (
          <TouchableOpacity
            key={card.id}
            onPress={() => setSelectedCard(card.id)}
            style={[
              styles.cardWrapper,
              selectedCard === card.id && styles.selectedCard,
            ]}
          >
            <ScratchCard
              imageUrl={card.imageUrl}
              isSelected={selectedCard === card.id}
            />
          </TouchableOpacity>
        ))}
      </ScrollView>
    </LinearGradient>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  title: {
    fontSize: 32,
    fontWeight: 'bold',
    color: 'white',
    textAlign: 'center',
    marginTop: 20,
  },
  subtitle: {
    fontSize: 18,
    color: 'white',
    textAlign: 'center',
    marginBottom: 20,
  },
  cardsContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'center',
    gap: 15,
    paddingBottom: 20,
  },
  cardWrapper: {
    width: '45%',
    aspectRatio: 1,
    borderRadius: 15,
    overflow: 'hidden',
  },
  selectedCard: {
    transform: [{ scale: 1.05 }],
    elevation: 5,
  },
});
