import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, ScrollView } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import ScratchCard from '../components/ScratchCard';

export default function GameScreen() {
  const [selectedCard, setSelectedCard] = useState(null);

  const cards = [
    { id: 1, title: 'Romantic Dinner', content: 'Cook a special meal together' },
    { id: 2, title: 'Adventure Time', content: 'Go on a surprise hiking trip' },
    { id: 3, title: 'Movie Night', content: 'Watch your favorite film together' },
    { id: 4, title: 'Dance Party', content: 'Learn a new dance together' },
    { id: 5, title: 'Art Session', content: 'Paint each other portraits' },
    { id: 6, title: 'Spa Day', content: 'Give each other relaxing massages' },
  ];

  return (
    <LinearGradient
      colors={['#FF6B6B', '#4ECDC4']}
      style={styles.container}
    >
      <Text style={styles.title}>Adventure Cards</Text>
      <Text style={styles.subtitle}>Scratch to reveal your next adventure!</Text>

      <ScrollView contentContainerStyle={styles.cardsContainer}>
        {cards.map((card) => (
          <TouchableOpacity
            key={card.id}
            onPress={() => setSelectedCard(card.id)}
            style={[
              styles.cardWrapper,
              selectedCard === card.id && styles.selectedCard,
            ]}
          >
            <ScratchCard
              title={card.title}
              content={card.content}
              isSelected={selectedCard === card.id}
            />
          </TouchableOpacity>
        ))}
      </ScrollView>
    </LinearGradient>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  title: {
    fontSize: 32,
    fontWeight: 'bold',
    color: 'white',
    textAlign: 'center',
    marginTop: 20,
  },
  subtitle: {
    fontSize: 18,
    color: 'white',
    textAlign: 'center',
    marginBottom: 20,
  },
  cardsContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'center',
    gap: 15,
    paddingBottom: 20,
  },
  cardWrapper: {
    width: '45%',
    aspectRatio: 1,
    borderRadius: 15,
    overflow: 'hidden',
  },
  selectedCard: {
    transform: [{ scale: 1.05 }],
    elevation: 5,
  },
});
