import React, { useState } from 'react';
import { View, Image, Text, StyleSheet, Animated, ImageSourcePropType } from 'react-native';
import { PanGestureHandler } from 'react-native-gesture-handler';

interface ScratchCardProps {
  image: ImageSourcePropType;
  isSelected: boolean;
}

export default function ScratchCard({ image, isSelected }: ScratchCardProps) {
  const [scratched, setScratched] = useState(false);
  const [scratchProgress, setScratchProgress] = useState(0);

  const handleScratch = (event) => {
    if (isSelected) {
      setScratchProgress((prev) => Math.min(prev + 0.1, 1));
      if (scratchProgress >= 0.7) {
        setScratched(true);
      }
    }
  };

  return (
    <PanGestureHandler onGestureEvent={handleScratch}>
      <View style={styles.container}>
        {scratched ? (
          <Image 
            source={image} // This could also be a GIF
            style={styles.image}
            resizeMode="cover"
          />
        ) : (
          <Image 
            source={require('../')} // Replace with your GIF file
            style={styles.overlay}
            resizeMode="cover"
          />
        )}
      </View>
    </PanGestureHandler>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    borderRadius: 15,
    overflow: 'hidden',
    backgroundColor: 'white',
  },
  image: {
    width: '100%',
    height: '100%',
  },
  overlay: {
    ...StyleSheet.absoluteFillObject,
    justifyContent: 'center',
    alignItems: 'center',
  },
});
