import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, ScrollView, Modal, Button } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import ScratchCard from '../components/ScratchCard';
import { images } from '../../utils/images';

export default function GameScreen() {
  const [selectedCard, setSelectedCard] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);

  const cards = [
    { id: 1, image: images.image1 },
    { id: 2, image: images.image2 },
    { id: 3, image: images.image3 },
    { id: 4, image: images.image4 },
    { id: 5, image: images.image5 },
    { id: 6, image: images.image6 },
  ];

  const handleCardPress = (cardId) => {
    setSelectedCard(cardId);
    setModalVisible(true);
  };

  const closeModal = () => {
    setModalVisible(false);
    setSelectedCard(null);
  };

  return (
    <LinearGradient
      colors={['#FF6B6B', '#4ECDC4']}
      style={styles.container}
    >
      <Text style={styles.title}>Adventure Cards</Text>
      <Text style={styles.subtitle}>Scratch to reveal your next adventure!</Text>

      <ScrollView contentContainerStyle={styles.cardsContainer}>
        {cards.map((card) => (
          <TouchableOpacity
            key={card.id}
            onPress={() => handleCardPress(card.id)}
            style={styles.cardWrapper}
          >
            <View style={styles.cardPreview}>
              <Text style={styles.cardText}>Click to Scratch</Text>
            </View>
          </TouchableOpacity>
        ))}
      </ScrollView>

      <Modal
        visible={modalVisible}
        transparent={true}
        animationType="slide"
        onRequestClose={closeModal}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalContent}>
            {selectedCard && (
              <ScratchCard
                image={cards.find((card) => card.id === selectedCard).image}
                isSelected={true}
              />
            )}
            <Button title="Close" onPress={closeModal} />
          </View>
        </View>
      </Modal>
    </LinearGradient>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  title: {
    fontSize: 32,
    fontWeight: 'bold',
    color: 'white',
    textAlign: 'center',
    marginTop: 20,
  },
  subtitle: {
    fontSize: 18,
    color: 'white',
    textAlign: 'center',
    marginBottom: 20,
  },
  cardsContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'center',
    gap: 15,
    paddingBottom: 20,
  },
  cardWrapper: {
    width: '45%',
    aspectRatio: 1,
    borderRadius: 15,
    overflow: 'hidden',
    backgroundColor: 'white',
    justifyContent: 'center',
    alignItems: 'center',
    elevation: 2,
  },
  cardPreview: {
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#FF6B6B',
    flex: 1,
  },
  cardText: {
    color: 'white',
    fontWeight: 'bold',
    fontSize: 16,
  },
  modalContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
  },
  modalContent: {
    width: '80%',
    backgroundColor: 'white',
    borderRadius: 15,
    padding: 20,
    alignItems: 'center',
  },
});
